    public static string GetConfig(string code)
    {
        SystemConfigList all = SystemConfigList.GetAll();
        SystemConfig info = all.FirstOrDefault(x => x.Code.ToLower() == code.ToLower());
        if (info != null)
        {
            return info.Value;
        }

        return null;
